mapper.select:
SELECT yi.id, yi.`name`, yi.description, yi.price, yi.num, yi.type_id, yt.`name` typeName, yi.production_date, yi.production_place, yi.guarantee_date, yi.extent, yi.create_time, yi.create_by, yi.update_time, yi.update_by, yi.share_status, yi.share_value, yi.share_cycle, yi.thumbnail FROM yw_idle yi LEFT JOIN yw_type yt ON yi.type_id = yt.id
yi.name LIKE CONCAT("%",#{name},"%")
yi.type_id = #{typeId}
yi.price > #{minPrice}
yi.price < #{maxPrice}
yi.production_place like CONCAT("%",#{productionPlace},"%")
yi.share_status = #{shareStatus}
yi.share_value > #{minShareValue}
yi.share_value < #{maxShareValue}
ORDER BY #{idleOrder} #{orderType}
limit #{page},#{pageSize}

SELECT
count(1)
FROM
yw_idle yi
LEFT JOIN yw_type yt ON yi.type_id = yt.id


        yi.name LIKE CONCAT("%",#{name},"%")


        yi.type_id = #{typeId}


        yi.price > #{minPrice}


        yi.price < #{maxPrice}


        yi.production_place like CONCAT("%",#{productionPlace},"%")


        yi.share_status = #{shareStatus}


        yi.share_value > #{minShareValue}


        yi.share_value < #{maxShareValue}

mapper.update:
UPDATE yw_idle


        share_status = #{shareStatus},


        share_value = #{shareValue},


        share_cycle = #{shareCycle}


WHERE id = #{idleId}
